selector  {
    border: 5px solid;
    border-image: linear-gradient(90deg, rgba(60, 74, 83, 0), #08EC83, rgba(60, 74, 83, 0)) 1;
    border-top: none;
    border-left: none;
    border-right: none;
}

#seu-botao-id { /* Substitua por ID do seu botão */
  -webkit-animation: pulse 1s infinite;
  animation: pulse 1s infinite;
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.1);
  }
  100% {
    transform: scale(1);
  }
}

